import{_ as e,c as t,o as s,a as r}from"./app.7e8aa18b.js";const w=JSON.parse('{"title":"Permissions","description":"","frontmatter":{},"headers":[{"level":2,"title":"Default Values","slug":"default-values"},{"level":2,"title":"Permission Types","slug":"permission-types"}],"relativePath":"translate/appwrite/advanced/permissions.md"}'),a={name:"translate/appwrite/advanced/permissions.md"},i=r('<h1 id="permissions" tabindex="-1">Permissions <a class="header-anchor" href="#permissions" aria-hidden="true">#</a></h1><p>Appwrite permission mechanism offers a simple and yet flexible way to manage which users, teams, or roles can access a specific resource of your project, like documents and files.</p><p>Appwrite \u6743\u9650\u673A\u5236\u63D0\u4F9B\u4E86\u4E00\u79CD\u7B80\u5355\u800C\u7075\u6D3B\u7684\u65B9\u5F0F\u6765\u7BA1\u7406\u54EA\u4E9B\u7528\u6237\u3001\u56E2\u961F\u6216\u89D2\u8272\u53EF\u4EE5\u8BBF\u95EE\u9879\u76EE\u7684\u7279\u5B9A\u8D44\u6E90\uFF0C\u4F8B\u5982\u6587\u6863\u548C\u6587\u4EF6\u3002</p><p>Using permissions, you can decide that only user A and user B will have read access to a specific database document, while user C and team X will be the only ones with write access.</p><p>\u4F7F\u7528 permissions\uFF0C\u60A8\u53EF\u4EE5\u51B3\u5B9A\u53EA\u6709\u7528\u6237 A \u548C\u7528\u6237 B \u5BF9\u7279\u5B9A\u6570\u636E\u5E93\u6587\u6863\u5177\u6709\u8BFB\u53D6\u6743\u9650\uFF0C\u800C\u7528\u6237 C \u548C\u56E2\u961F X \u5C06\u662F\u552F\u4E00\u5177\u6709\u5199\u5165\u6743\u9650\u7684\u4EBA\u3002</p><p>As the name suggests, read permission will allow users and teams to view a resource while with write permission, they will be able to both update or delete it.</p><p>\u987E\u540D\u601D\u4E49\uFF0C\u8BFB\u53D6\u6743\u9650\u5C06\u5141\u8BB8\u7528\u6237\u548C\u56E2\u961F\u67E5\u770B\u8D44\u6E90\uFF0C\u800C\u5177\u6709\u5199\u5165\u6743\u9650\u65F6\uFF0C\u4ED6\u4EEC\u5C06\u80FD\u591F\u66F4\u65B0\u6216\u5220\u9664\u5B83\u3002</p><p>Read and write permissions can be given to specific users, entire teams, or only to a particular group of role members inside a team.</p><p>\u8BFB\u5199\u6743\u9650\u53EF\u4EE5\u6388\u4E88\u7279\u5B9A\u7528\u6237\u3001\u6574\u4E2A\u56E2\u961F\u6216\u4EC5\u6388\u4E88\u56E2\u961F\u5185\u7684\u7279\u5B9A\u89D2\u8272\u6210\u5458\u7EC4\u3002</p><p>A project user can only grant a resource with permissions he or she owns. For example, if a user is trying to share a document with a team, he or she are not members of, they will encounter a 401 not authorized error.</p><p>\u9879\u76EE\u7528\u6237\u53EA\u80FD\u6388\u4E88\u4ED6\u62E5\u6709\u7684\u6743\u9650\u7684\u8D44\u6E90\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u7528\u6237\u5C1D\u8BD5\u4E0E\u56E2\u961F\u5171\u4EAB\u6587\u6863\uFF0C\u4F46\u4ED6\u6216\u5979\u4E0D\u662F\u56E2\u961F \u200B\u200B \u6210\u5458\uFF0C\u4ED6\u4EEC\u5C06\u9047\u5230 401 \u672A\u6388\u6743\u9519\u8BEF</p><div class="tip custom-block"><p class="custom-block-title">Appwrite Resource</p><p>An Appwrite resource can be a database collection, database document, or a storage file. Each resource has both read and write permissions to define who can access it.</p><p>Appwrite \u8D44\u6E90\u53EF\u4EE5\u662F\u6570\u636E\u5E93\u96C6\u5408\u3001\u6570\u636E\u5E93\u6587\u6863\u6216\u5B58\u50A8\u6587\u4EF6\u3002\u6BCF\u4E2A\u8D44\u6E90\u90FD\u6709\u8BFB\u5199\u6743\u9650\u6765\u5B9A\u4E49\u8C01\u53EF\u4EE5\u8BBF\u95EE\u5B83\u3002</p><p>Using the Appwrite permissions mechanism, you can share resources between users, teams, and members with different roles.</p><p>\u4F7F\u7528 Appwrite \u6743\u9650\u673A\u5236\uFF0C\u60A8\u53EF\u4EE5\u5728\u4E0D\u540C\u89D2\u8272\u7684\u7528\u6237\u3001\u56E2\u961F\u548C\u6210\u5458\u4E4B\u95F4\u5171\u4EAB\u8D44\u6E90\u3002</p></div><h2 id="default-values" tabindex="-1">Default Values <a class="header-anchor" href="#default-values" aria-hidden="true">#</a></h2><p>When not providing a resource with read or write permissions, the default value will be empty. When a read or write permissions is missing, no one will be granted access control to the resource.</p><p>\u5F53\u4E0D\u63D0\u4F9B\u5177\u6709\u8BFB\u53D6\u6216\u5199\u5165\u6743\u9650\u7684\u8D44\u6E90\u65F6\uFF0C\u9ED8\u8BA4\u503C\u4E3A\u7A7A\u3002\u5F53\u7F3A\u5C11\u8BFB\u53D6\u6216\u5199\u5165\u6743\u9650\u65F6\uFF0C\u5C06\u4E0D\u4F1A\u6388\u4E88\u4EFB\u4F55\u4EBA\u5BF9\u8BE5\u8D44\u6E90\u7684\u8BBF\u95EE\u63A7\u5236\u6743\u3002</p><div class="tip custom-block"><p class="custom-block-title">Note</p><p>You will need an active session to handle database transactions for your collections.</p><p>\u60A8\u5C06\u9700\u8981\u4E00\u4E2A\u6D3B\u52A8\u4F1A\u8BDD\u6765\u5904\u7406\u96C6\u5408\u7684\u6570\u636E\u5E93\u4E8B\u52A1\u3002</p><p>Using the Appwrite permissions mechanism, you can share resources between users, teams, and members with different roles.</p><p>\u4F7F\u7528 Appwrite \u6743\u9650\u673A\u5236\uFF0C\u60A8\u53EF\u4EE5\u5728\u4E0D\u540C\u89D2\u8272\u7684\u7528\u6237\u3001\u56E2\u961F\u548C\u6210\u5458\u4E4B\u95F4\u5171\u4EAB\u8D44\u6E90\u3002</p></div><h2 id="permission-types" tabindex="-1">Permission Types <a class="header-anchor" href="#permission-types" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:center;">Type</th><th>Description</th></tr></thead><tbody><tr><td style="text-align:center;">role:all</td><td>\u901A\u914D\u7B26\u6743\u9650\u3002\u6388\u4E88\u4EFB\u4F55\u4EBA\u8BFB\u53D6\u6216\u5199\u5165\u6743\u9650</td></tr><tr><td style="text-align:center;">user:[USER_ID]</td><td>\u901A\u8FC7\u5176 UID \u8BBE\u7F6E\u7279\u5B9A\u7528\u6237\u3002</td></tr><tr><td style="text-align:center;">team:[TEAM_ID]</td><td>\u7279\u5B9A\u56E2\u961F\u7684\u4EFB\u4F55\u6210\u5458\u3002 \u8981\u83B7\u5F97\u6B64\u6743\u9650\uFF0C\u7528\u6237\u5FC5\u987B\u662F\u56E2\u961F\u521B\u5EFA\u8005\uFF08\u6240\u6709\u8005\uFF09\uFF0C\u6216\u8005\u63A5\u6536\u5E76\u63A5\u53D7\u52A0\u5165\u6B64\u56E2\u961F\u7684\u9080\u8BF7\u3002</td></tr><tr><td style="text-align:center;">team:[TEAM_ID]/[ROLE]</td><td>\u5728\u56E2\u961F\u4E2D\u62E5\u6709\u7279\u5B9A\u89D2\u8272\u7684\u4EFB\u4F55\u6210\u5458,\u8981\u83B7\u5F97\u6B64\u6743\u9650\uFF0C\u7528\u6237\u5FC5\u987B\u662F\u7279\u5B9A\u56E2\u961F\u7684\u6210\u5458\u5E76\u5177\u6709\u5206\u914D\u7ED9\u4ED6\u7684\u7ED9\u5B9A\u89D2\u8272\u3002\u9080\u8BF7\u7528\u6237\u6210\u4E3A\u56E2\u961F\u6210\u5458\u65F6\u53EF\u4EE5\u5206\u914D\u56E2\u961F\u89D2\u8272</td></tr><tr><td style="text-align:center;">member:[MEMBER_ID]</td><td>\u56E2\u961F\u7684\u7279\u5B9A\u6210\u5458,\u4E0E\u57FA\u672C\u7528\u6237\u6743\u9650\u4E0D\u540C\uFF0C\u6B64\u6743\u9650\u4EC5\u5728\u7528\u6237\u4ECD\u662F\u7279\u5B9A\u56E2\u961F\u7684\u6D3B\u8DC3\u6210\u5458\u65F6\u624D\u6709\u6548\u3002\u8981\u67E5\u770B\u7528\u6237\u6210\u5458 ID\uFF0C\u8BF7\u83B7\u53D6\u56E2\u961F\u6210\u5458\u5217\u8868\u3002</td></tr><tr><td style="text-align:center;">role:guest</td><td>\u4EFB\u4F55\u6765\u5BBE\u7528\u6237\u3002\u767B\u5F55\u7528\u6237\u65E0\u6743\u8BBF\u95EE\u6B64\u89D2\u8272\u3002</td></tr><tr><td style="text-align:center;">role:member</td><td>\u4EFB\u4F55\u767B\u5F55\u7684\u7528\u6237\u3002\u767B\u5F55\u7528\u6237\u662F\u5177\u6709\u6709\u6548\u4F1A\u8BDD\u7684\u7528\u6237\u3002\u767B\u5F55\u7528\u6237\u65E0\u6743\u8BBF\u95EE <code>role:guest</code> \u89D2\u8272\u3002\u5BF9\u4E8E\u901A\u914D\u7B26\u8BBF\u95EE\uFF0C\u4F7F\u7528 <code>*</code> \u89D2\u8272\u3002</td></tr></tbody></table>',18),o=[i];function n(p,d,l,c,m,h){return s(),t("div",null,o)}var f=e(a,[["render",n]]);export{w as __pageData,f as default};
